import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import backendApi from './api';
function Login({ setUserInfo }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(backendApi + '/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (data.message === "No such username or password") {
        alert(data.message);
      }
      console.log(data);
      if (response.ok) {
        // Authentication successful
        if (data.userType === "manager") {
          setUserInfo({userName: username, userPassword: password, userType: "manager"});
        }
        if (data.userType === "admin") {
          setUserInfo({userName: username, userPassword: password, userType: "admin"});
        }
        if (data.userType === "analyst") {
          setUserInfo({userName: username, userPassword: password, userType: "analyst"});
        }



        // Redirect to dashboard or appropriate page after successful login
      } else {
        // Handle authentication failure (e.g., show error message)
        console.error('Authentication failed');
        alert('Authentication failed');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
